  async createProperty(insertProperty: InsertProperty): Promise<Property> {
    // Validate wave assignment if a wave is being assigned
    if (insertProperty.agentId && insertProperty.waveId) {
      const validation = await this.validateWaveAssignment(insertProperty.agentId, insertProperty.waveId);
      if (!validation.valid) {
        throw new Error(validation.message || 'Wave assignment not allowed');
      }
    }

    // Generate unique slug
    const baseSlug = generatePropertySlug(insertProperty);
    let slug = baseSlug;
    let counter = 1;

    // Check slug uniqueness manually since we need async
    while (await this.isSlugTaken(slug)) {
      slug = `${baseSlug}-${counter}`;
      counter++;
      if (counter > 1000) {
        slug = `${baseSlug}-${Date.now()}`;
        break;
      }
    }

    const propertyData = {
      ...insertProperty,
      slug
    };

    const [property] = await db()
      .insert(properties)
      .values(propertyData as any)
      .returning();
    return property;
  }

  async updateProperty(id: string, updateProperty: Partial<InsertProperty>): Promise<Property | undefined> {
    // Get current property to check if wave assignment is changing
    const currentProperty = await this.getProperty(id);
    if (!currentProperty) {
      return undefined;
    }

    // Validate wave assignment if a wave is being assigned or changed
    if (updateProperty.waveId !== undefined && currentProperty.agentId) {
      const validation = await this.validateWaveAssignment(currentProperty.agentId, updateProperty.waveId);
      if (!validation.valid) {
        throw new Error(validation.message || 'Wave assignment not allowed');
      }
    }

    // Check if slug-affecting fields have changed and regenerate slug if needed
    const slugAffectingFields = ['title', 'city', 'type', 'listingType', 'bedrooms'];
    const shouldRegenerateSlug = slugAffectingFields.some(field => 
      updateProperty[field as keyof typeof updateProperty] !== undefined
    );

    let updateData = { ...updateProperty, updatedAt: new Date() } as any;

    if (shouldRegenerateSlug) {
      const updatedPropertyData = { ...currentProperty, ...updateProperty };
      const baseSlug = generatePropertySlug(updatedPropertyData);
      let slug = baseSlug;
      let counter = 1;

      // Check slug uniqueness manually since we need async
      while (await this.isSlugTaken(slug, id)) {
        slug = `${baseSlug}-${counter}`;
        counter++;
        if (counter > 1000) {
          slug = `${baseSlug}-${Date.now()}`;
          break;
        }
      }
      updateData.slug = slug;
    }

    const [property] = await db()
      .update(properties)
      .set(updateData)
      .where(eq(properties.id, id))
      .returning();
    return property || undefined;
  }

