  // Wave balance tracking methods
  async getUserWaveUsage(userId: string): Promise<number> {
    return this.properties.filter(p => 
      p.agentId === userId && 
      p.waveId && 
      p.waveId !== 'no-wave'
    ).length;
  }

  async getUserRemainingWaves(userId: string): Promise<number> {
    const user = this.users.find(u => u.id === userId);
    if (!user) return 0;

    // Admin and super_admin have unlimited waves
    if (user.role === 'admin' || user.role === 'super_admin') {
      return 999999;
    }

    const currentUsage = await this.getUserWaveUsage(userId);
    const remaining = (user.waveBalance || 0) - currentUsage;
    return Math.max(0, remaining);
  }

  async validateWaveAssignment(userId: string, waveId: string | null): Promise<{ valid: boolean; message?: string }> {
    if (!waveId || waveId === 'no-wave') {
      return { valid: true };
    }

    const user = this.users.find(u => u.id === userId);
    if (!user) {
      return { valid: false, message: 'User not found' };
    }

    // Admin and super_admin have unlimited waves
    if (user.role === 'admin' || user.role === 'super_admin') {
      return { valid: true };
    }

    const remainingWaves = await this.getUserRemainingWaves(userId);
    if (remainingWaves <= 0) {
      return { 
        valid: false, 
        message: `No wave assignments remaining. Current balance: ${user.waveBalance || 0}` 
      };
    }

    return { valid: true };
  }

  // Function to update wave balance for all users with 0 balance (in-memory)
  async updateUsersWithZeroWaveBalance(): Promise<number> {
    let updatedCount = 0;
    this.users.forEach(user => {
      if (user.role === 'user' && (user.waveBalance || 0) === 0) {
        user.waveBalance = 10;
        updatedCount++;
      }
    });

    console.log(`Updated wave balance for ${updatedCount} users`);
    return updatedCount;
  }

  // Method to clear all properties
  async clearAllProperties(): Promise<number> {
    const count = this.properties.length;
    this.properties = [];
    this.favorites = []; // Also clear favorites since they reference properties
    this.inquiries = []; // Also clear inquiries since they reference properties
    this.searchHistories = []; // Also clear search histories
    console.log(`Cleared ${count} properties and related data`);
    return count;
  }

  // Currency exchange rates
  async getCurrencyRates(): Promise<CurrencyRate[]> {
    return [...this.currencyRates].sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());
  }

  async getActiveCurrencyRates(): Promise<CurrencyRate[]> {
    return this.currencyRates.filter(rate => rate.isActive).sort((a, b) => a.toCurrency.localeCompare(b.toCurrency));
  }

  async getCurrencyRate(fromCurrency: string, toCurrency: string): Promise<CurrencyRate | undefined> {
    return this.currencyRates
      .filter(rate => 
        rate.fromCurrency === fromCurrency && 
        rate.toCurrency === toCurrency && 
        rate.isActive
      )
      .sort((a, b) => new Date(b.effectiveDate).getTime() - new Date(a.effectiveDate).getTime())[0];
  }

  async createCurrencyRate(rate: InsertCurrencyRate): Promise<CurrencyRate> {
    const newRate: CurrencyRate = {
      id: `rate-${Date.now()}`,
      ...rate,
      effectiveDate: new Date(),
      createdAt: new Date(),
      updatedAt: new Date()
    };
    this.currencyRates.push(newRate);
    return newRate;
  }

  async updateCurrencyRate(id: string, rate: UpdateCurrencyRate): Promise<CurrencyRate | undefined> {
    const index = this.currencyRates.findIndex(r => r.id === id);
    if (index === -1) return undefined;

    this.currencyRates[index] = {
      ...this.currencyRates[index],
      ...rate,
      updatedAt: new Date()
    };
    return this.currencyRates[index];
  }

  async deactivateCurrencyRate(id: string): Promise<boolean> {
    const index = this.currencyRates.findIndex(r => r.id === id);
    if (index === -1) return false;

    this.currencyRates[index] = {
      ...this.currencyRates[index],
      isActive: false,
      updatedAt: new Date()
    };
    return true;
  }

  async convertPrice(amount: number, fromCurrency: string, toCurrency: string): Promise<number> {
    // If same currency, return original amount
    if (fromCurrency === toCurrency) {
      return amount;
    }

    // Get exchange rate
    const rate = await this.getCurrencyRate(fromCurrency, toCurrency);
    if (!rate) {
      // If no direct rate found, try reverse rate
      const reverseRate = await this.getCurrencyRate(toCurrency, fromCurrency);
      if (reverseRate) {
        return amount / parseFloat(reverseRate.rate);
      }
      // If no rate found, return original amount (fallback)
      return amount;
    }

    return amount * parseFloat(rate.rate);
  }
}

