@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  html {
    height: 100%;
  }

  #root {
    min-height: 100vh;
    min-height: 100dvh; /* Dynamic viewport height for mobile */
  }

  /* Restrict scrolling for home/map page only */
  .map-page body {
    height: 100vh;
    height: 100dvh; /* Dynamic viewport height for mobile */
    overflow: hidden;
  }

  .map-page html {
    height: 100%;
    overflow: hidden;
  }

  .map-page #root {
    height: 100vh;
    height: 100dvh; /* Dynamic viewport height for mobile */
    overflow: hidden;
  }

  /* Arabic Language Support - using higher specificity instead of !important */
  html [lang="ar"]:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(.company-name), 
  html [lang="ar"] *:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(svg):not([data-lucide]):not(.company-name), 
  html .arabic-font:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(.company-name), 
  html .arabic-font *:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(svg):not([data-lucide]):not(.company-name) {
    font-family: var(--font-arabic);
    direction: rtl;
    text-align: right;
  }

  /* Kurdish Language Support - using higher specificity instead of !important */
  html [lang="kur"]:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(.company-name):not(button):not([role="button"]), 
  html [lang="kur"] *:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(svg):not([data-lucide]):not(.company-name):not(button):not([role="button"]), 
  html .kurdish-font:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(.company-name):not(button):not([role="button"]), 
  html .kurdish-font *:not(i):not(.fa):not(.fas):not(.far):not(.fal):not(.fab):not([class*="icon"]):not(svg):not([data-lucide]):not(.company-name):not(button):not([role="button"]) {
    font-family: var(--font-kurdish);
    direction: rtl;
    text-align: right;
  }
}

/* ========================================
   RESPONSIVE TYPOGRAPHY UTILITY CLASSES
   ======================================== */

@layer utilities {
  /* Heading classes with responsive scaling */
  .heading-1 {
    @apply text-4xl md:text-5xl lg:text-display-sm font-bold;
    letter-spacing: var(--tracking-tighter);
    line-height: var(--leading-4xl);
  }

  .heading-2 {
    @apply text-3xl md:text-4xl lg:text-5xl font-bold;
    letter-spacing: var(--tracking-tight);
    line-height: var(--leading-3xl);
  }

  .heading-3 {
    @apply text-2xl md:text-3xl lg:text-4xl font-semibold;
    letter-spacing: var(--tracking-tight);
    line-height: var(--leading-2xl);
  }

  .heading-4 {
    @apply text-xl md:text-2xl lg:text-3xl font-semibold;
    letter-spacing: var(--tracking-normal);
    line-height: var(--leading-xl);
  }

  .heading-5 {
    @apply text-lg md:text-xl lg:text-2xl font-medium;
    letter-spacing: var(--tracking-normal);
    line-height: var(--leading-lg);
  }

  .heading-6 {
    @apply text-base md:text-lg lg:text-xl font-medium;
    letter-spacing: var(--tracking-normal);
    line-height: var(--leading-base);
  }

  /* Display text for hero sections and large headings */
  .display-xl {
    font-size: var(--text-display-xl);
    line-height: var(--leading-display-xl);
    letter-spacing: var(--tracking-tighter);
    @apply font-black;
  }

  .display-lg {
    font-size: var(--text-display-lg);
    line-height: var(--leading-display-lg);
    letter-spacing: var(--tracking-tighter);
    @apply font-black;
  }

  .display-md {
    font-size: var(--text-display-md);
    line-height: var(--leading-display-md);
    letter-spacing: var(--tracking-tight);
    @apply font-bold;
  }

  .display-sm {
    font-size: var(--text-display-sm);
    line-height: var(--leading-display-sm);
    letter-spacing: var(--tracking-tight);
    @apply font-bold;
  }

  /* Body text classes with optimal reading experience */
  .body-xl {
    @apply text-xl leading-relaxed;
    max-width: var(--max-width-prose-xl);
  }

  .body-lg {
    @apply text-lg leading-relaxed;
    max-width: var(--max-width-prose-lg);
  }

  .body-base {
    @apply text-base leading-relaxed;
    max-width: var(--max-width-prose);
  }

  .body-sm {
    @apply text-sm leading-normal;
    max-width: var(--max-width-prose);
  }

  /* Caption and small text */
  .caption {
    @apply text-sm text-muted-foreground;
    letter-spacing: var(--tracking-wide);
  }

  .overline {
    @apply text-xs uppercase font-medium;
    letter-spacing: var(--tracking-wider);
  }

  /* Responsive text utilities */
  .text-responsive-xs { font-size: var(--text-xs); }
  .text-responsive-sm { font-size: var(--text-sm); }
  .text-responsive-base { font-size: var(--text-base); }
  .text-responsive-lg { font-size: var(--text-lg); }
  .text-responsive-xl { font-size: var(--text-xl); }
  .text-responsive-2xl { font-size: var(--text-2xl); }
  .text-responsive-3xl { font-size: var(--text-3xl); }
  .text-responsive-4xl { font-size: var(--text-4xl); }
  .text-responsive-5xl { font-size: var(--text-5xl); }

  /* Leading utilities */
  .leading-responsive-xs { line-height: var(--leading-xs); }
  .leading-responsive-sm { line-height: var(--leading-sm); }
  .leading-responsive-base { line-height: var(--leading-base); }
  .leading-responsive-lg { line-height: var(--leading-lg); }
  .leading-responsive-xl { line-height: var(--leading-xl); }
  .leading-responsive-2xl { line-height: var(--leading-2xl); }
  .leading-responsive-3xl { line-height: var(--leading-3xl); }
  .leading-responsive-4xl { line-height: var(--leading-4xl); }
  .leading-responsive-5xl { line-height: var(--leading-5xl); }

  /* Typography scale for consistent hierarchy */
  .scale-xs { 
    @apply text-xs leading-responsive-xs; 
    letter-spacing: var(--tracking-wide);
  }
  .scale-sm { 
    @apply text-sm leading-responsive-sm; 
    letter-spacing: var(--tracking-normal);
  }
  .scale-base { 
    @apply text-base leading-responsive-base; 
    letter-spacing: var(--tracking-normal);
  }
  .scale-lg { 
    @apply text-lg leading-responsive-lg; 
    letter-spacing: var(--tracking-normal);
  }
  .scale-xl { 
    @apply text-xl leading-responsive-xl; 
    letter-spacing: var(--tracking-tight);
  }
  .scale-2xl { 
    @apply text-2xl leading-responsive-2xl; 
    letter-spacing: var(--tracking-tight);
  }
  .scale-3xl { 
    @apply text-3xl leading-responsive-3xl; 
    letter-spacing: var(--tracking-tight);
  }
  .scale-4xl { 
    @apply text-4xl leading-responsive-4xl; 
    letter-spacing: var(--tracking-tighter);
  }
  .scale-5xl { 
    @apply text-5xl leading-responsive-5xl; 
    letter-spacing: var(--tracking-tighter);
  }

  /* Content width utilities for optimal reading */
  .prose-responsive {
    max-width: var(--max-width-prose);
  }

  .prose-responsive-lg {
    max-width: var(--max-width-prose-lg);
  }

  .prose-responsive-xl {
    max-width: var(--max-width-prose-xl);
  }

  /* Mobile-specific typography optimizations */
  @media (max-width: 640px) {
    .mobile-heading-1 {
      @apply text-3xl font-bold;
      line-height: 1.2;
      letter-spacing: var(--tracking-tight);
    }

    .mobile-heading-2 {
      @apply text-2xl font-bold;
      line-height: 1.3;
      letter-spacing: var(--tracking-normal);
    }

    .mobile-body {
      @apply text-base leading-relaxed;
      line-height: 1.6;
    }
  }

  /* High DPI display optimizations */
  @media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
    .crisp-text {
      -webkit-font-smoothing: antialiased;
      -moz-osx-font-smoothing: grayscale;
    }
  }
}

/* Custom Leaflet Popup Styles with Animations */
.leaflet-popup-content-wrapper {
  border-radius: 16px !important;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.12), 0 8px 25px rgba(0, 0, 0, 0.08) !important;
  border: 1px solid rgba(255, 255, 255, 0.2) !important;
  padding: 0 !important;
  backdrop-filter: blur(8px) !important;
  background: rgba(255, 255, 255, 0.98) !important;
  animation: popupSlideIn 0.3s ease-out forwards !important;
}

.leaflet-popup-content {
  margin: 0 !important;
  padding: 0 !important;
  font-family: var(--font-sans) !important;
  overflow: hidden !important;
  border-radius: 16px !important;
}

.leaflet-popup-tip {
  background: rgba(255, 255, 255, 0.98) !important;
  border: none !important;
  box-shadow: none !important;
  backdrop-filter: blur(8px) !important;
}

.custom-popup .leaflet-popup-close-button {
  top: 8px !important;
  right: 8px !important;
  width: 32px !important;
  height: 32px !important;
  font-size: 16px !important;
  color: white !important;
  background: linear-gradient(135deg, rgba(0, 0, 0, 0.7) 0%, rgba(0, 0, 0, 0.5) 100%) !important;
  border-radius: 50% !important;
  text-align: center !important;
  line-height: 32px !important;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.3), 0 2px 8px rgba(0, 0, 0, 0.2) !important;
  border: none !important;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1) !important;
  z-index: 1000 !important;
  font-weight: 600 !important;
  backdrop-filter: blur(8px) !important;
  cursor: pointer !important;
  display: flex !important;
  align-items: center !important;
  justify-content: center !important;
}

.custom-popup .leaflet-popup-close-button:hover {
  background: linear-gradient(135deg, rgba(220, 38, 38, 0.9) 0%, rgba(185, 28, 28, 0.8) 100%) !important;
  transform: scale(1.1) rotate(90deg) !important;
  box-shadow: 0 6px 25px rgba(220, 38, 38, 0.4), 0 3px 12px rgba(0, 0, 0, 0.3) !important;
  border: none !important;
  color: white !important;
}

.custom-popup .leaflet-popup-close-button:active {
  transform: scale(0.95) rotate(90deg) !important;
  transition: all 0.1s ease !important;
}

/* Popup Animation Keyframes */
@keyframes popupSlideIn {
  0% {
    transform: scale(0.85) translateY(10px) !important;
    opacity: 0 !important;
  }
  100% {
    transform: scale(1) translateY(0) !important;
    opacity: 1 !important;
  }
}

@keyframes popupSlideOut {
  0% {
    transform: scale(1) translateY(0) !important;
    opacity: 1 !important;
  }
  100% {
    transform: scale(0.95) translateY(-5px) !important;
    opacity: 0 !important;
  }
}

/* Professional Responsive Popup Styles */
.responsive-popup {
  width: 320px;
  max-width: 92vw;
  background: linear-gradient(135deg, #ffffff 0%, #fafbfc 100%);
  border-radius: 16px;
  overflow: hidden;
  position: relative;
}

.responsive-popup::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, hsl(25 95% 53%), hsl(74 43% 70%), hsl(25 95% 65%));
  animation: gradientShimmer 2s ease-in-out infinite;
}

.responsive-popup .popup-image {
  width: 100%;
  height: 160px;
  margin-bottom: 0;
  overflow: hidden;
  position: relative;
  background: linear-gradient(135deg, #f1f5f9 0%, #e2e8f0 100%);
}

.responsive-popup .popup-image img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
  transition: transform 0.3s ease;
}

.responsive-popup .popup-image:hover img {
  transform: scale(1.05);
}

.responsive-popup .popup-content {
  padding: 20px;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(10px);
}

.responsive-popup .popup-title {
  font-weight: 700;
  font-size: 16px;
  margin: 0 0 8px 0;
  line-height: 1.4;
  color: #0f172a;
  word-wrap: break-word;
  letter-spacing: -0.025em;
}

.responsive-popup .popup-address {
  font-size: 13px;
  color: #64748b;
  margin: 0 0 12px 0;
  word-wrap: break-word;
  font-weight: 500;
  display: flex;
  align-items: center;
  gap: 6px;
}

.responsive-popup .popup-address::before {
  content: '📍';
  font-size: 12px;
}

.responsive-popup .popup-price {
  font-weight: 800;
  font-size: 20px;
  color: hsl(25 95% 45%);
  margin: 0 0 12px 0;
  text-shadow: 0 1px 2px rgba(30, 64, 175, 0.1);
}

.responsive-popup .popup-details {
  font-size: 13px;
  color: #475569;
  margin: 0 0 20px 0;
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  font-weight: 500;
}

.responsive-popup .popup-details span {
  background: hsla(25, 95%, 53%, 0.08);
  padding: 4px 8px;
  border-radius: 6px;
  border: 1px solid hsla(25, 95%, 53%, 0.1);
  transition: all 0.2s ease;
}

.responsive-popup .popup-details span:hover {
  background: hsla(25, 95%, 53%, 0.12);
  transform: translateY(-1px);
}

.responsive-popup .popup-button {
  width: 100%;
  background: linear-gradient(135deg, hsl(25 95% 53%) 0%, hsl(25 95% 60%) 100%);
  color: white;
  border: none;
  padding: 14px 20px;
  border-radius: 12px;
  font-size: 14px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  touch-action: manipulation;
  position: relative;
  overflow: hidden;
  box-shadow: 0 4px 14px hsla(25, 95%, 53%, 0.3);
  letter-spacing: 0.5px;
}

.responsive-popup .popup-button::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.responsive-popup .popup-button:hover {
  transform: translateY(-2px);
