        title={homeMeta.title}
        description={homeMeta.description}
        keywords={homeMeta.keywords.join(', ')}
        ogImage={
          featuredProperties && featuredProperties.length > 0
            ? featuredProperties[0].images?.[0]
            : `${window.location.origin}/mapestate-og-image.jpg`
        }
        canonicalUrl={undefined}
        structuredData={[getHomepageStructuredData(), generateFAQStructuredData(language)]}
        breadcrumbs={[
          { name: t('nav.home'), url: '/' }
        ]}
      />
      {/* Full Screen Map Section */}
      <section className="h-full w-full relative">
        <Suspense
          fallback={
            <div className="h-full w-full flex items-center justify-center bg-muted rounded-lg">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
                <p className="text-muted-foreground">{t("home.loadingMap")}</p>
              </div>
            </div>
          }
        >
          <PropertyMap
            properties={(mapProperties || []) as any}
            filters={mapFilters}
            onFilterChange={handleMapFilterChange}
            onPropertyClick={(property) => {
              const identifier = property.slug || property.id;
              window.location.href = `/property/${identifier}`;
            }}
            onPropertySelect={(property) => {
              // Handle property selection for highlighting
              setHighlightedPropertyId(property.id);
            }}
            userId={user?.id}
