                      <Info className="h-4 w-4" style={{ color: "#FF7800" }} />
                      <span>{t("nav.about")}</span>
                    </DropdownMenuItem>
                    <DropdownMenuItem
                      className="flex items-center gap-2 cursor-pointer"
