                    <SelectContent className="z-[10000]">
                      <SelectItem value="all">
                        <span className="flex items-center gap-2">
                          <Filter
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.allTypes")}
                        </span>
                      </SelectItem>
                      <SelectItem value="sale">
                        <span className="flex items-center gap-2">
                          <Tag
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.forSale")}
                        </span>
                      </SelectItem>
                      <SelectItem value="rent">
                        <span className="flex items-center gap-2">
                          <Key
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.forRent")}
                        </span>
                      </SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {/* Property Type */}
