                      <SelectValue placeholder={t("filter.allProperties")} />
                    </SelectTrigger>
                    <SelectContent className="z-[10000]">
                      <SelectItem value="all">
                        <span className="flex items-center gap-2">
                          <Home
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.allProperties")}
                        </span>
                      </SelectItem>
                      <SelectItem value="house">
                        <span className="flex items-center gap-2">
                          <Home
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.house")}
                        </span>
                      </SelectItem>
                      <SelectItem value="apartment">
                        <span className="flex items-center gap-2">
                          <Building
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.apartment")}
                        </span>
                      </SelectItem>
                      <SelectItem value="villa">
                        <span className="flex items-center gap-2">
                          <University
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.villa")}
                        </span>
                      </SelectItem>
                      <SelectItem value="land">
                        <span className="flex items-center gap-2">
                          <Mountain
                            className="h-4 w-4"
                            style={{ color: "#FF7800" }}
                          />
                          {t("filter.land")}
                        </span>
                      </SelectItem>
                    </SelectContent>
