import { useState, useEffect } from 'react';
import { useLocation } from 'wouter';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage, FormDescription } from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Checkbox } from '@/components/ui/checkbox';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useToast } from '@/hooks/use-toast';
import { useAuth } from '@/hooks/useAuth';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import type { User, CurrencyRate, InsertCurrencyRate, UpdateCurrencyRate } from '@shared/schema';
import { SUPPORTED_LANGUAGES, LANGUAGE_NAMES, insertCurrencyRateSchema, updateCurrencyRateSchema } from '@shared/schema';
import { 
  Shield, Users, Building2, Settings, Plus, Edit, Trash2, 
  LogOut, UserPlus, Key, BarChart3, Activity, Calendar,
  Search, Filter, MoreVertical, AlertTriangle, Eye, MapPin,
  Home, DollarSign, ImageIcon, Languages, CreditCard
} from 'lucide-react';
import { CustomerAnalytics } from '@/components/CustomerAnalytics';
import { 
  useCurrencyRates, 
  useCreateCurrencyRate, 
  useUpdateCurrencyRate, 
  useDeleteCurrencyRate,
  type CreateCurrencyRateForm,
  type UpdateCurrencyRateForm
} from '@/hooks/use-currency-rates';

const createUserSchema = z.object({
  username: z.string().min(3, 'Username must be at least 3 characters'),
  email: z.string().email('Please enter a valid email'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  role: z.enum(['user', 'agent', 'admin']),
  firstName: z.string().optional(),
  lastName: z.string().optional(),
  phone: z.string().optional(),
  avatar: z.string().optional(),
  waveBalance: z.number().min(0, 'Wave balance must be 0 or greater').default(0),
  expiresAt: z.string().optional(),
  isVerified: z.boolean().default(false),
  allowedLanguages: z.array(z.enum(SUPPORTED_LANGUAGES)).default(['en']).optional(),
});

const editUserSchema = z.object({
  username: z.string().min(3, 'Username must be at least 3 characters'),
  email: z.string().email('Please enter a valid email'),
  password: z.string().min(6, 'Password must be at least 6 characters').optional(),
  role: z.enum(['user', 'agent', 'admin']),
  firstName: z.string().optional(),
  lastName: z.string().optional(),
  phone: z.string().optional(),
  avatar: z.string().optional(),
  waveBalance: z.number().min(0, 'Wave balance must be 0 or greater').default(0),
  expiresAt: z.string().optional(),
  isVerified: z.boolean().default(false),
  allowedLanguages: z.array(z.enum(SUPPORTED_LANGUAGES)).optional(),
});

type CreateUserForm = z.infer<typeof createUserSchema>;
type EditUserForm = z.infer<typeof editUserSchema>;

// Extend shared schemas with form-specific validation
const createCurrencyRateFormSchema = insertCurrencyRateSchema.extend({
  toCurrency: z.enum(['IQD', 'EUR', 'AED'], {
    required_error: 'Please select a target currency',
  }),
  rate: z.coerce.number().positive('Exchange rate must be greater than 0'),
});

const updateCurrencyRateFormSchema = updateCurrencyRateSchema.extend({
  rate: z.coerce.number().positive('Exchange rate must be greater than 0'),
});

type CreateCurrencyRateFormData = z.infer<typeof createCurrencyRateFormSchema>;
type UpdateCurrencyRateFormData = z.infer<typeof updateCurrencyRateFormSchema>;

// Helper functions for expiration
const calculateDaysUntilExpiration = (expiresAt: string | Date | null): number | null => {
  if (!expiresAt) return null;
  const now = new Date();
  const expiration = expiresAt instanceof Date ? expiresAt : new Date(expiresAt);
  const diffInMs = expiration.getTime() - now.getTime();
  return Math.ceil(diffInMs / (1000 * 60 * 60 * 24));
};

const getExpirationStatus = (daysUntilExpiration: number | null): { status: string; color: string; bgColor: string } => {
  if (daysUntilExpiration === null) {
    return { status: 'No Expiration', color: 'text-green-600', bgColor: 'bg-green-100' };
  }

  if (daysUntilExpiration < 0) {
    return { status: 'Expired', color: 'text-red-600', bgColor: 'bg-red-100' };
  }

  if (daysUntilExpiration <= 3) {
    return { status: `${daysUntilExpiration} days left`, color: 'text-red-600', bgColor: 'bg-red-100' };
  }

  if (daysUntilExpiration <= 7) {
    return { status: `${daysUntilExpiration} days left`, color: 'text-yellow-600', bgColor: 'bg-yellow-100' };
  }

  return { status: `${daysUntilExpiration} days left`, color: 'text-green-600', bgColor: 'bg-green-100' };
};

export default function AdminDashboard() {
  const [, navigate] = useLocation();
  const { user, logout } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isCreateUserOpen, setIsCreateUserOpen] = useState(false);
  const [isEditUserOpen, setIsEditUserOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<User | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [roleFilter, setRoleFilter] = useState<string>('all');
  const [avatarFile, setAvatarFile] = useState<File | null>(null);
  const [avatarPreview, setAvatarPreview] = useState<string>('');
  const [showPasswords, setShowPasswords] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(10);
  const [selectedCustomer, setSelectedCustomer] = useState<User | null>(null);
  const [isCustomerDetailsOpen, setIsCustomerDetailsOpen] = useState(false);

  // Currency rate management state
  const [isCreateCurrencyRateOpen, setIsCreateCurrencyRateOpen] = useState(false);
  const [isEditCurrencyRateOpen, setIsEditCurrencyRateOpen] = useState(false);
  const [editingCurrencyRate, setEditingCurrencyRate] = useState<CurrencyRate | null>(null);

  // Redirect if not admin or super admin
  useEffect(() => {
    if (user && user.role !== 'admin' && user.role !== 'super_admin') {
      toast({
        title: 'Access Denied',
        description: 'You do not have permission to access this page.',
        variant: 'destructive',
      });
      navigate('/');
    }
  }, [user, navigate, toast]);

  // Redirect to login if not authenticated
  useEffect(() => {
    if (!user) {
      navigate('/admin/login?unauthorized=true');
    }
  }, [user, navigate]);

  const form = useForm<CreateUserForm>({
    resolver: zodResolver(createUserSchema),
    defaultValues: {
      username: '',
      email: '',
      password: '',
      role: 'user',
      firstName: '',
      lastName: '',
      phone: '',
      avatar: '',
      waveBalance: 10,
      expiresAt: '',
      isVerified: false,
      allowedLanguages: ['en'],
    },
  });

  const editForm = useForm<EditUserForm>({
    resolver: zodResolver(editUserSchema),
    defaultValues: {
      username: '',
      email: '',
      password: '',
      role: 'user',
      firstName: '',
      lastName: '',
      phone: '',
      avatar: '',
      waveBalance: 0,
      expiresAt: '',
      isVerified: false,
      allowedLanguages: ['en'],
    },
  });

  // Currency rate forms
  const currencyRateForm = useForm<CreateCurrencyRateFormData>({
    resolver: zodResolver(createCurrencyRateFormSchema),
    defaultValues: {
      toCurrency: 'IQD',
      rate: 0,
      isActive: true,
    },
  });

  const editCurrencyRateForm = useForm<UpdateCurrencyRateFormData>({
    resolver: zodResolver(updateCurrencyRateFormSchema),
    defaultValues: {
      rate: 0,
      isActive: true,
    },
  });

  // Currency rate hooks
  const { data: currencyRates = [], isLoading: currencyRatesLoading } = useCurrencyRates();
  const createCurrencyRateMutation = useCreateCurrencyRate();
  const updateCurrencyRateMutation = useUpdateCurrencyRate();
  const deleteCurrencyRateMutation = useDeleteCurrencyRate();

  // Handle avatar file selection
  const handleAvatarChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Check file size (limit to 5MB)
      const maxSize = 5 * 1024 * 1024; // 5MB in bytes
      if (file.size > maxSize) {
        toast({
          title: 'File too large',
          description: 'Please select an image smaller than 5MB',
          variant: 'destructive',
        });
        event.target.value = ''; // Clear the input
        return;
      }

      // Check file type
      if (!file.type.startsWith('image/')) {
        toast({
          title: 'Invalid file type',
          description: 'Please select an image file',
          variant: 'destructive',
        });
        event.target.value = ''; // Clear the input
        return;
      }

      setAvatarFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        const result = reader.result as string;
        setAvatarPreview(result);
        form.setValue('avatar', result);
        editForm.setValue('avatar', result);
      };
      reader.readAsDataURL(file);
    }
  };

  // Reset avatar when dialog closes
  const resetAvatarUpload = () => {
    setAvatarFile(null);
    setAvatarPreview('');
    form.setValue('avatar', '');
    editForm.setValue('avatar', '');
  };

  // Fetch all users
  const { data: users = [], isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ['/api/admin/users'],
    enabled: user?.role === 'admin' || user?.role === 'super_admin',
  });

  // Fetch users with passwords for admins
  const { data: usersWithPasswords = [], isLoading: usersWithPasswordsLoading } = useQuery<any[]>({
    queryKey: ['/api/admin/users/with-passwords'],
    enabled: (user?.role === 'admin' || user?.role === 'super_admin') && showPasswords,
    retry: false,
  });

  // Fetch customer properties when a customer is selected
  const { data: customerProperties = [], isLoading: customerPropertiesLoading } = useQuery<any[]>({
    queryKey: ['/api/users', selectedCustomer?.id, 'properties'],
    enabled: !!selectedCustomer?.id,
    retry: false,
  });

  // Create user mutation
  const createUserMutation = useMutation({
    mutationFn: async (userData: CreateUserForm) => {
      // Prepare data for backend - keep expiresAt as string or remove if empty
      const transformedData: any = { ...userData };

      // Handle expiresAt field properly
      if (userData.expiresAt && userData.expiresAt.trim() !== '') {
        // Ensure it's a valid date string in ISO format
        const dateObj = new Date(userData.expiresAt);
        if (!isNaN(dateObj.getTime())) {
          transformedData.expiresAt = userData.expiresAt; // Keep as string for backend processing
        } else {
          delete transformedData.expiresAt; // Invalid date, remove it
        }
      } else {
        delete transformedData.expiresAt; // Empty or null, remove it
      }

      // Only super admins can set language permissions
      if (user?.role !== 'super_admin') {
        delete transformedData.allowedLanguages;
      }

      const response = await apiRequest('POST', '/api/admin/users', transformedData);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      setIsCreateUserOpen(false);
      form.reset();
      resetAvatarUpload();
      toast({
        title: 'Success',
        description: 'User created successfully',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to create user',
        variant: 'destructive',
      });
    },
  });

  // Edit user mutation
  const editUserMutation = useMutation({
    mutationFn: async ({ id, userData }: { id: string; userData: EditUserForm }) => {
      // Prepare data for backend - keep expiresAt as string or remove if empty
      const transformedData: any = { ...userData };

      // Handle expiresAt field properly
      if (userData.expiresAt && userData.expiresAt.trim() !== '') {
        // Ensure it's a valid date string in ISO format
        const dateObj = new Date(userData.expiresAt);
        if (!isNaN(dateObj.getTime())) {
          transformedData.expiresAt = userData.expiresAt; // Keep as string for backend processing
        } else {
          delete transformedData.expiresAt; // Invalid date, remove it
        }
      } else {
        delete transformedData.expiresAt; // Empty or null, remove it
      }

      // Only super admins can set language permissions
      if (user?.role !== 'super_admin') {
        delete transformedData.allowedLanguages;
      }

      const response = await apiRequest('PUT', `/api/admin/users/${id}`, transformedData);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      setIsEditUserOpen(false);
      setEditingUser(null);
      editForm.reset();
      resetAvatarUpload();
      toast({
        title: 'Success',
        description: 'User updated successfully',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to update user',
        variant: 'destructive',
      });
    },
  });

  // Delete user mutation
  const deleteUserMutation = useMutation({
    mutationFn: async (userId: string) => {
      const response = await apiRequest('DELETE', `/api/admin/users/${userId}`);
      return await response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/admin/users'] });
      toast({
        title: 'Success',
        description: 'User deleted successfully',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Error',
        description: error.message || 'Failed to delete user',
        variant: 'destructive',
      });
    },
  });

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/admin/login');
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Logout failed',
        variant: 'destructive',
      });
    }
  };

  const onCreateUser = async (data: CreateUserForm) => {
    await createUserMutation.mutateAsync(data);
  };

  const onEditUser = async (data: EditUserForm) => {
    if (editingUser) {
      await editUserMutation.mutateAsync({ id: editingUser.id, userData: data });
    }
  };

  const handleEditUser = (user: User) => {
    setEditingUser(user);

    // Get the current password if available
    const userWithPassword = usersWithPasswords.find(u => u.id === user.id);
    const currentPassword = userWithPassword?.password || '';

    // Format expiresAt date for datetime-local input
    let formattedExpiresAt = '';
    if (user.expiresAt) {
      const date = new Date(user.expiresAt);
      // Format as YYYY-MM-DDTHH:MM for datetime-local input
      formattedExpiresAt = date.toISOString().slice(0, 16);
    }

    editForm.reset({
      username: user.username,
      email: user.email,
      password: currentPassword, // Show current password if available
      role: user.role as 'user' | 'agent' | 'admin',
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      phone: user.phone || '',
      avatar: user.avatar || '',
      waveBalance: user.waveBalance || 0,
      expiresAt: formattedExpiresAt,
      isVerified: user.isVerified || false,
      allowedLanguages: (user.allowedLanguages as Array<typeof SUPPORTED_LANGUAGES[number]>) || ['en'],
    });
    setAvatarPreview(user.avatar || '');
    setIsEditUserOpen(true);
  };

  // Currency rate handlers
  const onCreateCurrencyRate = async (data: CreateCurrencyRateFormData) => {
    await createCurrencyRateMutation.mutateAsync({
      toCurrency: data.toCurrency,
      rate: data.rate.toString(), // Convert to string for API
      isActive: data.isActive ?? false,
    });
    setIsCreateCurrencyRateOpen(false);
    currencyRateForm.reset();
  };

  const onEditCurrencyRate = async (data: UpdateCurrencyRateFormData) => {
    if (editingCurrencyRate) {
      await updateCurrencyRateMutation.mutateAsync({ 
        id: editingCurrencyRate.id, 
        data: {
          rate: data.rate.toString(), // Convert to string for API
          isActive: data.isActive ?? false,
        }
      });
      setIsEditCurrencyRateOpen(false);
      setEditingCurrencyRate(null);
      editCurrencyRateForm.reset();
    }
  };

  const handleEditCurrencyRate = (currencyRate: CurrencyRate) => {
    setEditingCurrencyRate(currencyRate);
    editCurrencyRateForm.reset({
      rate: Number(currencyRate.rate), // Convert to number for form
      isActive: currencyRate.isActive || false,
    });
    setIsEditCurrencyRateOpen(true);
  };

  const handleDeleteCurrencyRate = async (id: string) => {
    if (confirm('Are you sure you want to deactivate this currency rate? This action cannot be undone.')) {
      await deleteCurrencyRateMutation.mutateAsync(id);
    }
  };

  const handleDeleteUser = async (userId: string, username: string) => {
    if (window.confirm(`Are you sure you want to delete user "${username}"?`)) {
      await deleteUserMutation.mutateAsync(userId);
    }
  };

  const handleViewCustomerDetails = (customer: User) => {
    setSelectedCustomer(customer);
    setIsCustomerDetailsOpen(true);
  };

  // Reset page to 1 when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, roleFilter]);

  // Filter users based on search and role
  const filteredUsers = users.filter(u => {
    const matchesSearch = !searchTerm || 
      u.username.toLowerCase().includes(searchTerm.toLowerCase()) ||
      u.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (u.firstName && u.firstName.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (u.lastName && u.lastName.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesRole = roleFilter === 'all' || u.role === roleFilter;

    return matchesSearch && matchesRole;
  });

  // Pagination calculations
  const totalPages = Math.ceil(filteredUsers.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const paginatedUsers = filteredUsers.slice(startIndex, endIndex);
  const showPagination = filteredUsers.length > itemsPerPage;

  // Statistics
  const stats = {
    totalUsers: users.length,
    agents: users.filter(u => u.role === 'agent').length,
    customers: users.filter(u => u.role === 'user').length,
    admins: users.filter(u => u.role === 'admin').length,
  };

  if (!user || (user.role !== 'admin' && user.role !== 'super_admin')) {
    return null;
