                          <td className="py-4 px-4 font-medium">{rate.toCurrency}</td>
                          <td className="py-4 px-4 font-mono text-lg">{Number(rate.rate).toLocaleString(undefined, { minimumFractionDigits: 2, maximumFractionDigits: 6 })}</td>
