      bedrooms: 1,
      bathrooms: 1,
      area: 1,
      address: '',
      city: '',
      country: 'Iraq',
      latitude: undefined,
      longitude: undefined,
      images: [],
      amenities: [],
      features: [],
      contactPhone: user?.phone || '',
