import { useEffect, useRef, useState } from 'react';
import { useTranslation } from '@/lib/i18n';

// Reverse geocoding function using Nominatim (OpenStreetMap)
const reverseGeocode = async (lat: number, lng: number): Promise<LocationData> => {
  try {
    const response = await fetch(
      `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lng}&addressdetails=1`
    );

    if (!response.ok) {
      throw new Error('Geocoding failed');
    }

    const data = await response.json();

    // Extract address components
    const address = data.display_name || '';
    const addressParts = data.address || {};

    // Build a clean address string
    const addressComponents = [];
    if (addressParts.house_number) addressComponents.push(addressParts.house_number);
    if (addressParts.road) addressComponents.push(addressParts.road);
    if (addressParts.neighbourhood) addressComponents.push(addressParts.neighbourhood);

    const cleanAddress = addressComponents.length > 0 
      ? addressComponents.join(' ')
      : (addressParts.road || addressParts.suburb || address.split(',')[0] || '');

    const city = addressParts.city || 
                 addressParts.town || 
                 addressParts.village || 
                 addressParts.municipality || 
                 addressParts.state_district || '';

    const country = addressParts.country || '';

    return {
      lat,
      lng,
      address: cleanAddress,
      city,
      country
    };
  } catch (error) {
    console.warn('Reverse geocoding failed:', error);
    return { lat, lng };
  }
};

interface LocationData {
  lat: number;
  lng: number;
  address?: string;
  city?: string;
  country?: string;
}

interface LocationSelectionMapProps {
  onLocationSelect: (data: LocationData) => void;
  selectedLocation?: { lat: number; lng: number } | null;
  className?: string;
}

export default function LocationSelectionMap({ 
  onLocationSelect, 
  selectedLocation, 
  className = '' 
}: LocationSelectionMapProps) {
  const { t } = useTranslation();
  const mapRef = useRef<HTMLDivElement>(null);
  const mapInstanceRef = useRef<any>(null);
  const markerRef = useRef<any>(null);
  const [isMapLoaded, setIsMapLoaded] = useState(false);
  const [userLocation, setUserLocation] = useState<{lat: number, lng: number} | null>(null);

  // Get user's location for auto-centering the map
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });
        },
        (error) => {
          console.warn('Geolocation failed:', error.message);
          // Will fall back to default location (Kurdistan)
        },
        {
          enableHighAccuracy: false,
          timeout: 10000,
          maximumAge: 300000 // 5 minutes
        }
      );
    }
  }, []);

  useEffect(() => {
    if (!mapRef.current || mapInstanceRef.current) return;

    let checkLeafletInterval: NodeJS.Timeout | null = null;
    let timeoutRef: NodeJS.Timeout | null = null;

    // Initialize the map
    const initMap = () => {
      if (typeof window !== 'undefined' && (window as any).L && mapRef.current) {
        const L = (window as any).L;

        try {
          // Ensure container is ready
          if (!mapRef.current.offsetParent) {
            setTimeout(initMap, 100);
            return;
          }

          // Center on user's location if available, otherwise Kurdistan/Iraq region
          const defaultLocation = [36.1911, 44.0094]; // Kurdistan/Iraq fallback
          const centerLocation = userLocation ? [userLocation.lat, userLocation.lng] : defaultLocation;
          const zoomLevel = userLocation ? 10 : 8; // Closer zoom if we have user location

          const map = L.map(mapRef.current).setView(centerLocation, zoomLevel);

          // Add OpenStreetMap tiles
          L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: '© OpenStreetMap contributors'
          }).addTo(map);

          // Add click event to map
          map.on('click', async (e: any) => {
            try {
              const { lat, lng } = e.latlng;

              // Remove existing marker
              if (markerRef.current) {
                map.removeLayer(markerRef.current);
              }

              // Add new marker with loading popup
              markerRef.current = L.marker([lat, lng]).addTo(map);
              markerRef.current.bindPopup('Getting address...').openPopup();

              // Perform reverse geocoding
              const locationData = await reverseGeocode(lat, lng);

              // Update marker popup with address
              if (locationData.address) {
                markerRef.current.setPopupContent(`📍 ${locationData.address}`);
              }

              // Call the callback with full location data
              onLocationSelect(locationData);
            } catch (error) {
              console.warn('Error handling map click:', error);
              // Fallback: still call with basic location data
              onLocationSelect({ lat: e.latlng.lat, lng: e.latlng.lng });
            }
          });

          mapInstanceRef.current = map;
          setIsMapLoaded(true);

          // Invalidate size to ensure proper rendering
          setTimeout(() => {
            if (mapInstanceRef.current) {
              mapInstanceRef.current.invalidateSize();
            }
          }, 100);

        } catch (error) {
          console.error('Error initializing location selection map:', error);
          setIsMapLoaded(false);
        }
      }
    };

    // Check if Leaflet is already loaded
    if ((window as any).L) {
      initMap();
    } else {
      // Wait for Leaflet to load
      checkLeafletInterval = setInterval(() => {
        if ((window as any).L) {
          if (checkLeafletInterval) {
            clearInterval(checkLeafletInterval);
            checkLeafletInterval = null;
          }
          initMap();
        }
      }, 100);

      // Cleanup interval after 10 seconds
      timeoutRef = setTimeout(() => {
        if (checkLeafletInterval) {
          clearInterval(checkLeafletInterval);
          checkLeafletInterval = null;
        }
        console.warn('Leaflet failed to load within 10 seconds');
      }, 10000);
    }

    // Cleanup function
    return () => {
      if (checkLeafletInterval) {
        clearInterval(checkLeafletInterval);
      }
      if (timeoutRef) {
        clearTimeout(timeoutRef);
      }

      if (mapInstanceRef.current) {
        try {
          mapInstanceRef.current.remove();
        } catch (error) {
          console.warn('Error cleaning up location selection map:', error);
        }
        mapInstanceRef.current = null;
      }

      if (markerRef.current) {
        markerRef.current = null;
      }
    };
  }, [onLocationSelect, userLocation]);

  // Re-center map when user location is detected
  useEffect(() => {
    if (mapInstanceRef.current && userLocation && isMapLoaded) {
      // Smoothly animate to user's location
      mapInstanceRef.current.setView([userLocation.lat, userLocation.lng], 10, {
        animate: true,
        duration: 1.5
      });
    }
  }, [userLocation, isMapLoaded]);

  // Update marker when selectedLocation changes externally
  useEffect(() => {
    if (mapInstanceRef.current && selectedLocation && isMapLoaded) {
      const L = (window as any).L;

      // Remove existing marker
      if (markerRef.current) {
        mapInstanceRef.current.removeLayer(markerRef.current);
      }

      // Add new marker at selected location
      markerRef.current = L.marker([selectedLocation.lat, selectedLocation.lng])
        .addTo(mapInstanceRef.current);

      // Center map on the selected location
      mapInstanceRef.current.setView([selectedLocation.lat, selectedLocation.lng], 13);
    }
  }, [selectedLocation, isMapLoaded]);

  return (
    <div className={`relative ${className}`}>
      <div 
        ref={mapRef} 
        className="w-full h-full rounded-lg"
        style={{ minHeight: '400px' }}
      />

      {/* Overlay instructions */}
      <div className="absolute top-4 left-4 bg-white dark:bg-gray-800 p-3 rounded-lg shadow-lg border z-10 max-w-xs">
        <div className="flex items-center space-x-2 mb-2">
          <div className="h-2 w-2 bg-blue-500 rounded-full animate-pulse"></div>
          <span className="text-sm font-medium">Click to Select Location</span>
        </div>
        <p className="text-xs text-muted-foreground">
          Click anywhere on the map to mark your property's location
        </p>
      </div>

      {/* Loading overlay */}
      {!isMapLoaded && (
        <div className="absolute inset-0 bg-gray-100 dark:bg-gray-800 flex items-center justify-center rounded-lg">
          <div className="text-center">
            <div className="animate-spin h-8 w-8 border-4 border-blue-600 border-t-transparent rounded-full mx-auto mb-2"></div>
            <p className="text-sm text-muted-foreground">{t('common.loading')}</p>
          </div>
        </div>
      )}
    </div>
  );
}

client/src/components/image-upload.tsx.content:
import { useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Upload, X, Image as ImageIcon, AlertCircle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface ImageUploadProps {
  value: string[];
  onChange: (images: string[]) => void;
  maxFiles?: number;
  maxSize?: number; // in MB
  className?: string;
}

export default function ImageUpload({ 
  value = [], 
  onChange, 
  maxFiles = 10, 
  maxSize = 5,
  className = '' 
}: ImageUploadProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const { toast } = useToast();

  const convertToBase64 = (file: File): Promise<string> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
    });
  };

  const validateFile = (file: File): boolean => {
    if (!file.type.startsWith('image/')) {
      toast({
        title: 'Invalid file type',
        description: 'Please upload only image files (JPG, PNG, GIF, etc.)',
        variant: 'destructive',
      });
      return false;
    }

    if (file.size > maxSize * 1024 * 1024) {
      toast({
        title: 'File too large',
        description: `Please upload images smaller than ${maxSize}MB`,
        variant: 'destructive',
      });
      return false;
    }

    return true;
  };

  const handleFileUpload = useCallback(async (files: FileList) => {
    if (value.length + files.length > maxFiles) {
      toast({
        title: 'Too many files',
        description: `You can upload maximum ${maxFiles} images`,
        variant: 'destructive',
      });
      return;
    }

    setUploading(true);
    const newImages: string[] = [];

    try {
      for (let i = 0; i < files.length; i++) {
        const file = files[i];
        if (validateFile(file)) {
          const base64 = await convertToBase64(file);
          newImages.push(base64);
        }
      }

      if (newImages.length > 0) {
        onChange([...value, ...newImages]);
        toast({
          title: 'Images uploaded',
          description: `Successfully uploaded ${newImages.length} image(s)`,
        });
      }
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: 'Failed to process some images. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setUploading(false);
    }
  }, [value, onChange, maxFiles, maxSize, toast]);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files);
    }
  }, [handleFileUpload]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  }, []);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      handleFileUpload(files);
    }
    e.target.value = ''; // Reset input
  }, [handleFileUpload]);

  const removeImage = useCallback((index: number) => {
    const newImages = [...value];
    newImages.splice(index, 1);
    onChange(newImages);
    toast({
      title: 'Image removed',
      description: 'Image has been removed from the list',
    });
  }, [value, onChange, toast]);

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Upload Area */}
      <Card 
        className={`relative border-2 border-dashed transition-colors ${
          isDragging 
            ? 'border-blue-500 bg-blue-50 dark:bg-blue-900/20' 
            : 'border-gray-300 dark:border-gray-600 hover:border-gray-400 dark:hover:border-gray-500'
        }`}
      >
        <CardContent 
          className="p-8 text-center cursor-pointer"
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onClick={() => document.getElementById('file-input')?.click()}
        >
          <input
            id="file-input"
            type="file"
            multiple
            accept="image/*"
            className="hidden"
            onChange={handleFileInput}
            disabled={uploading || value.length >= maxFiles}
          />

          <div className="space-y-4">
            <div className="flex justify-center">
              {uploading ? (
                <div className="animate-spin h-8 w-8 border-4 border-blue-600 border-t-transparent rounded-full" />
              ) : (
                <Upload className={`h-8 w-8 ${isDragging ? 'text-blue-500' : 'text-gray-400'}`} />
              )}
            </div>

            <div>
              <p className="text-lg font-medium">
                {uploading ? 'Uploading images...' : 'Upload Property Images'}
              </p>
              <p className="text-sm text-muted-foreground mt-1">
                Drag & drop images here, or click to select files
              </p>
              <p className="text-xs text-muted-foreground mt-2">
                Supports JPG, PNG, GIF up to {maxSize}MB each. Maximum {maxFiles} images.
              </p>
            </div>

            {value.length > 0 && (
              <Badge variant="secondary" className="text-xs">
                {value.length}/{maxFiles} images uploaded
              </Badge>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Image Previews */}
      {value.length > 0 && (
        <div>
          <h4 className="text-sm font-medium mb-3 flex items-center gap-2">
            <ImageIcon className="h-4 w-4" />
            Uploaded Images ({value.length})
          </h4>
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            {value.map((image, index) => (
              <Card key={index} className="relative group overflow-hidden">
                <CardContent className="p-2">
                  <div className="relative aspect-square">
                    <img
                      src={image}
                      alt={`Property image ${index + 1}`}
                      className="w-full h-full object-cover rounded-md"
                      loading="lazy"
                      decoding="async"
                    />
                    <Button
                      variant="destructive"
                      size="icon"
                      className="absolute top-1 right-1 h-6 w-6 opacity-0 group-hover:opacity-100 transition-opacity"
                      onClick={(e) => {
                        e.stopPropagation();
                        removeImage(index);
                      }}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                    <Badge 
                      variant="secondary" 
                      className="absolute bottom-1 left-1 text-xs px-1 py-0"
                    >
                      #{index + 1}
                    </Badge>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Upload Limits Info */}
      {value.length === 0 && (
        <div className="flex items-center gap-2 text-xs text-muted-foreground bg-muted/50 p-3 rounded-lg">
          <AlertCircle className="h-4 w-4" />
          <span>
            Add multiple high-quality images to showcase your property. The first image will be used as the main photo.
          </span>
        </div>
      )}
    </div>
  );
}

client/src/components/profile-photo-uplo.content:
import { useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarImage, AvatarFallback } from '@/components/ui/avatar';
import { Upload, X, Camera } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';

interface ProfilePhotoUploadProps {
  value: string;
  onChange: (imageUrl: string) => void;
  currentUser?: {
    firstName?: string | null;
    lastName?: string | null;
    username: string;
  };
  maxSize?: number; // in MB
  className?: string;
}

export default function ProfilePhotoUpload({ 
  value = '', 
  onChange, 
  currentUser,
  maxSize = 2,
  className = '' 
}: ProfilePhotoUploadProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [uploading, setUploading] = useState(false);
  const { toast } = useToast();

  const convertToBase64 = (file: File): Promise<string> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
    });
  };

  const validateFile = (file: File): boolean => {
    if (!file.type.startsWith('image/')) {
      toast({
        title: 'Invalid file type',
        description: 'Please upload only image files (JPG, PNG, GIF, etc.)',
        variant: 'destructive',
      });
      return false;
    }

    if (file.size > maxSize * 1024 * 1024) {
      toast({
        title: 'File too large',
        description: `Please upload images smaller than ${maxSize}MB`,
        variant: 'destructive',
      });
      return false;
    }

    return true;
  };

  const handleFileUpload = useCallback(async (files: FileList) => {
    if (files.length === 0) return;

    const file = files[0]; // Only take the first file for profile photo
    if (!validateFile(file)) return;

    setUploading(true);

    try {
      const base64 = await convertToBase64(file);
      onChange(base64);
      toast({
        title: 'Profile photo uploaded',
        description: 'Your profile photo has been updated successfully',
      });
    } catch (error) {
      toast({
        title: 'Upload failed',
        description: 'Failed to process the image. Please try again.',
        variant: 'destructive',
      });
    } finally {
      setUploading(false);
    }
  }, [onChange, maxSize, toast]);

  const handleDrop = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files);
    }
  }, [handleFileUpload]);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  }, []);

  const handleFileInput = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files && files.length > 0) {
      handleFileUpload(files);
    }
    e.target.value = ''; // Reset input
  }, [handleFileUpload]);

  const removePhoto = useCallback(() => {
    onChange('');
    toast({
      title: 'Profile photo removed',
      description: 'Your profile photo has been removed',
    });
  }, [onChange, toast]);

  const getInitials = () => {
    if (currentUser?.firstName && currentUser?.lastName) {
      return `${currentUser.firstName[0]}${currentUser.lastName[0]}`;
    }
    return currentUser?.username?.[0] || 'U';
  };

  return (
    <div className={`space-y-4 ${className}`}>
      {/* Current Photo Display */}
      <div className="flex items-center space-x-4">
        <Avatar className="h-20 w-20">
          <AvatarImage src={value} />
          <AvatarFallback className="text-lg">
            {getInitials()}
          </AvatarFallback>
        </Avatar>
        <div className="flex flex-col space-y-2">
          <p className="text-sm font-medium">Profile Photo</p>
          <p className="text-xs text-muted-foreground">
            Upload a new photo or drag and drop
          </p>
        </div>
      </div>

      {/* Upload Area */}
      <div
        className={`
          relative border-2 border-dashed rounded-lg p-6 text-center transition-colors
          ${isDragging ? 'border-blue-500 bg-blue-50 dark:bg-blue-950' : 'border-gray-300 dark:border-gray-600'}
          ${uploading ? 'opacity-50 cursor-not-allowed' : 'cursor-pointer hover:border-blue-400'}
        `}
        onDrop={handleDrop}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
      >
        <input
          type="file"
          accept="image/*"
          onChange={handleFileInput}
          className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
          disabled={uploading}
          data-testid="input-profile-photo"
        />

        <div className="space-y-2">
          <Camera className="h-8 w-8 mx-auto text-gray-400" />
          <div>
            <p className="text-sm font-medium">
              {uploading ? 'Uploading...' : 'Click to upload or drag and drop'}
            </p>
            <p className="text-xs text-muted-foreground">
              PNG, JPG, GIF up to {maxSize}MB
            </p>
          </div>
        </div>
      </div>

      {/* Action Buttons */}
      <div className="flex space-x-2">
        <Button
          type="button"
          variant="outline"
          size="sm"
          onClick={() => (document.querySelector('input[data-testid="input-profile-photo"]') as HTMLInputElement)?.click()}
          disabled={uploading}
          data-testid="button-choose-photo"
        >
          <Upload className="h-4 w-4 mr-2" />
          Choose Photo
        </Button>

        {value && (
          <Button
            type="button"
            variant="outline"
            size="sm"
            onClick={removePhoto}
            disabled={uploading}
            data-testid="button-remove-photo"
          >
            <X className="h-4 w-4 mr-2" />
            Remove
          </Button>
        )}
      </div>
    </div>
  );
}

